import React from 'react'
import Input from './Input'
import MainButton from '../components/MainButton'
import axios from 'axios'

class SignInCard extends React.Component {
  state={}

  handleAuthenticate = (e) => {
    e.preventDefault()  
    const email = e.target.email.value
    const password = e.target.password.value

    axios.post('http://localhost:3001/authenticate', {
      "email":email,
      "password":password
    })
      .then( res => {
        if(res.auth_token){ this.props.setToken( res.auth_token ) }
      })
      .catch( err => console.log(err) )
  }


  render() {
      return ( 
          <form className="card card-content" onSubmit={this.handleAuthenticate}>

                  <span className="card-title center">Sign-In</span>
                  <Input type="email" label="email"/>
                  <Input type="password" label="password"/>
                  {/* <MainButton text="submit" addClass="center" path="/market"/> */}
                  <button type="submit">Submit</button>

          </form>
       )      
  }
}

export default SignInCard
