import React, { Component } from 'react'
import {NavLink} from 'react-router-dom'
import Input from '../components/Input'
import axios from 'axios'

class SignUp extends Component {
    state = { animate: "" }

    createUser = (e) => {
        e.preventDefault()
        const firstName = e.target.First_Name.value
        const lastName = e.target.Last_Name.value
        const email = e.target.Email.value
        const password = e.target.password.value
        const city = e.target.City.value
        const state = e.target.State.value
        const zip = e.target.Zip_Code.value
        axios.post('http://localhost:3001/customers',{
            state,
            city,
            zip_code: zip,
            customer: {
                first_name: firstName,
                last_name: lastName,
                mobile: '',
                street1: '',
                street2: '',
                email,
                password
            }
        })
            .then(res => {
              console.log( "WTF!? ")
              if(res.data.auth_token){
                  this.props.setToken(res.data.auth_token)
                  this.props.history.push('/dashboard')
              }
            })
            .catch( err => console.log( err ) )
    }
    componentDidMount() {
        this.setState({animate: "animated slideInRight"})
    }
    render() {
        return (
           <div
           className={this.state.animate}
           >
                <div className='indigo lighten-2'
                    style={{
                        textAlign: 'center',
                        padding: '10px 0'
                    }}
                >
                    <h3 className='white-text'
                        style = {{
                            padding: '0',
                            margin: '0'
                        }}
                    >Register to Ivest!</h3>
                </div>
                <div>
                   <NavLink
                    to='/market'
                    className='grey-text right'
                    style={{
                        padding: '0 10px'
                    }}
                    >Skip
                    </NavLink>
                </div>
                <div
                    style= {{
                        padding: '10px'
                    }}
                >
                    <NavLink
                        to='/'
                        className='btn indigo lighten-3 white-text z-depth-2'
                        style={{
                            margin: '0 10px'
                        }}
                        >
                        Personal Info
                    </NavLink>
                    <NavLink
                        to='/'
                        className='btn indigo lighten-3 white-text z-depth-2'
                        style={{
                            margin: '0 10px'
                        }}
                        >
                        Bank Info
                    </NavLink>
                </div>
            <form
                onSubmit={this.createUser}
                style={{
                    margin: '30px'
                }}
                >
                <div
                    className='container z-depth-2'
                    style={{
                        padding: '30px'
                    }}
                >
                    <Input type='text' label='First_Name'/>
                    <Input type='text' label='Last_Name'/>
                    <Input type='email' label='Email'/>
                    <Input type='password' label='password'/>
                    <Input type='text' label='Address 1'/>
                    <Input type='text' label='Address 2'/>
                    <Input type='text' label='City'/>
                    <Input label='State'/>
                    <Input type='number' label='Zip_Code'/>
                    <Input type='tel' label='Phone Number'/>
                <button
                    className="btn waves-effect waves-light indigo lighten-4"
                    type="submit"
                    name="action">
                        Submit
                    <i
                        className="material-icons left">
                            check
                    </i>
                </button>
                </div>
            </form>
           </div>
         )
    }
}

export default SignUp
